
public class Operadors {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int nombre1 = 10 ;
	System.out.println("nombre1 " + nombre1);
	int nombre2 = 8 ;
	System.out.println("nombre2 " + nombre2);
	int resultat1 = (nombre1 + nombre2) ;
	System.out.println("suma " + resultat1);
	
	int resultat2 = (nombre1 - nombre2);
	System.out.println("resta " + resultat2);
	
	int resultat3 = (nombre1 * nombre2);
	System.out.println("multiplcació " + resultat3);
	
	int resultat4 = (nombre1 / nombre2);
	System.out.println("divisió " + resultat4);
	
	int resultat5 = (nombre1 % nombre2);
	System.out.println("residu " + resultat5);
	
	nombre1 ++ ;
	System.out.println(nombre1);
	
	nombre1 -- ;
	System.out.println(nombre1);
	}

}
